import React, { useState } from 'react';
import { User, Linkedin, Mail, Phone } from 'lucide-react';
import './index.css';

function App() {
  const [showAbout, setShowAbout] = useState(false);

  const toggleAbout = () => {
    setShowAbout(!showAbout);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-background text-foreground">
      <header className="text-center">
        <h1 className="text-xl font-light">
          Hello, I'm <span className="font-bold text-foreground">Zhe Wei</span>.
        </h1>
        <div className="flex items-center justify-center gap-8 mt-6">
          <button onClick={toggleAbout} className="hover:text-accent">
            <User className="w-8 h-8" title="About" strokeWidth={1} />
          </button>
          <span className="w-px h-8 bg-foreground"></span>
          <a
            href="https://www.linkedin.com/in/czhew/"
            target="_blank"
            rel="noopener noreferrer"
            className="hover:text-accent"
          >
            <Linkedin className="w-8 h-8" title="LinkedIn" strokeWidth={1} />
          </a>
          <a href="mailto:czhewei.work@gmail.com" className="hover:text-accent">
            <Mail className="w-8 h-8" title="Mail" strokeWidth={1} />
          </a>
          <a href="tel:+6596747271" className="hover:text-accent">
            <Phone className="w-8 h-8" title="Phone" strokeWidth={1} />
          </a>
        </div>
      </header>
      {showAbout && (
        <main className="mt-8 animate-fade-in">
          <section className="max-w-3xl text-center">
            <p className="text-lg font-light">
              Hi, I'm Zhe Wei, a passionate DevOps Engineer with a keen interest in learning and building modern web applications. 
              I enjoy solving complex problems and continuously improving my skills in development and automation.
            </p>
          </section>
        </main>
      )}
    </div>
  );
}

export default App;
