import React, { useState } from 'react';
import { User, Linkedin, Mail, Phone } from 'lucide-react';
import './App.css';

function App() {
  const [showAbout, setShowAbout] = useState(false);

  const toggleAbout = () => {
    setShowAbout(!showAbout);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Hello, I'm Zhe Wei</h1>
        <div className="icon-container">
          <div className="about-section">
            <button onClick={toggleAbout} className="icon-button">
              <User className="icon" title="About" />
            </button>
          </div>
          <div className="contact-icons">
            <a href="https://www.linkedin.com/in/czhew/" target="_blank" rel="noopener noreferrer">
              <Linkedin className="icon" title="LinkedIn" />
            </a>
            <a href="mailto:czhewei.work@gmail.com">
              <Mail className="icon" title="Mail" />
            </a>
            <a href="tel:+6596747271">
              <Phone className="icon" title="Phone" />
            </a>
          </div>
        </div>
      </header>
      {showAbout && (
        <main>
          <section className="about-section-content">
            <p>
              Hi, I'm Zhe Wei, a passionate DevOps Engineer with a keen interest in learning and building modern web applications. 
              I enjoy solving complex problems and continuously improving my skills in development and automation.
            </p>
          </section>
        </main>
      )}
      <footer>
        Â© 2023 Zhe Wei. All rights reserved.
      </footer>
    </div>
  );
}

export default App;
